# Kruskal （克鲁斯卡尔）算法（加边法）
时间复杂度O(mlog(m)),用并查集维护
模板代码
```cpp
#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
const int inf=0x3f3f3f3f;
const int maxn=2e5+10;;
struct node {
    int x,y,z;
}edge[maxn];


bool cmp(node a,node b) {
    return a.z < b.z;
}


int fa[maxn];
int n,m;
int u,v,w; 
long long sum;

int Find(int x) {
    return x == fa[x] ? x : fa[x] = Find(fa[x]);
}

void solve() {
	// Kruskal 算法求最小生成树 
    cin>>n>>m;

    for(int i = 1; i <= m; i ++) {
        cin>>edge[i].x>>edge[i].y>>edge[i].z;
    }
    for(int i = 0; i <= n; i ++) {
        fa[i] = i;
    }
    sort(edge + 1,edge + 1 + m,cmp);
    // 每次加入一条最短的边
    for(int i = 1; i <= m; i ++) {
        int x = Find(edge[i].x);
        int y = Find(edge[i].y);
        if(x == y) continue;
        fa[y] = x;
        sum += edge[i].z;
    }
    int ans = 0;
    for(int i = 1; i <= n; i ++) {
        if(i == fa[i]) ans ++;
    }
    if(ans > 1) puts('impossible');
    else printf('%lld\n',sum);


}

signed main() {
	ios::sync_with_stdio(0);
	cin.tie(0);
	cout.tie(0);
	int t = 1;
	cin >> t;
    // cout<<'YES';
	while (t--) {
        // init();
		solve();
	}
	return 0;
}
